jQuery.noConflict(),
  jQuery(function ($) {
    var t = 100,
      e = $("a#scroll-to-top"),
      n = 600,
      a = "swing";
    e.hide(),
      $(window).scroll(function () {
        var n = $(document).scrollTop();
        n > t ? $(e).stop().fadeTo(300, 1) : $(e).stop().fadeTo(300, 0);
      }),
      $(e).click(function () {
        return $("html, body").animate({ scrollTop: 0 }, n, a), !1;
      }),
      $('a[href*="#"]:not([href="#"]):not([href^="#tab"])').click(function () {
        /* makes contact section appear if someone clicks on it */
        if (this.hash == '#contact') {
          $('#contact').css('display', 'block');
        }
        if (
          location.pathname.replace(/^\//, "") ==
            this.pathname.replace(/^\//, "") ||
          location.hostname == this.hostname
        ) {
          var t = $(this.hash);
          if (
            ((t = t.length ? t : $("[name=" + this.hash.slice(1) + "]")),
            t.length)
          )
            return (
              $("html,body").animate({ scrollTop: t.offset().top - document.getElementById("header").offsetHeight }, n, a), !1
            );
        }
      });
      /* handles the gform submit*/
      $('#gform').on('submit', function(e) {
        console.log("submitted!!");
        $('#gform *').fadeOut(n);
        setTimeout(function(){
          $('#gform').prepend('Your message has been sent!');
          document.getElementById("contact").style.padding = "2em 0 2em 0";
          document.getElementById("contact-title").style.margin = "0 0 1em 0";

        }, n);

      });
      

  });